mapper.select:
SELECT uId, username, loginId, email, password
from tbl_user
where loginId = #{loginId}

select count(*)
from tbl_user
where loginId = #{loginId}

select uId, username, loginId, email, password
from tbl_user
where email = #{email}

select count(*)
from tbl_user
where email = #{email}

select count(*)
from tbl_user
where username = #{username}
